pragma solidity ^0.5.0;

contract ToDoList {
    uint public taskCount = 0;

    struct Task {
        uint id;
        string content;
        bool completed;
    }

    mapping(uint=>Task) public tasks;

    constructor() public{
        createTask("Buy Ether");
        createTask("Sell Ether");
        createTask("Loose Money");
        createTask("Be Bilionnaire");
    }

    function createTask(string memory _content) public {
        taskCount ++;
        tasks[taskCount]= Task(taskCount,_content,false);
    }


}
